import React, {useState, useEffect} from 'react';
import { useGlobalContext } from '../context/Context';
import { useNavigate } from "react-router-dom";
 import axios from "axios";    

const CartContents = ({children}) => {
const {cart, deleted, setDeleted} = useGlobalContext()
    const [singleProductId, setSingleProductId] = useState([])

     const navigate = useNavigate();

    //  delete item from cart
const handleSubmit =  e => {
let isMounted = true;  
    e.preventDefault();
    setDeleted(true)
    const productId = e.target.id;
    let request = {method: 'POST',
            url: "http://localhost:8001/delete-cart",
            headers: {
                'Content-Type': 'application/json'
            },
            data: {
               productId: productId }}

            axios(request)
            .then(()=> {
              return () =>   setDeleted(true)
            })
     .catch(function (error) {
    if (error.response) {
      // The request was made and the server responded with a status code
      // that falls out of the range of 2xx
      console.log(error.response.data);
      console.log(error.response.status);
      console.log(error.response.headers);
    } else if (error.request) {
      // The request was made but no response was received
      // `error.request` is an instance of XMLHttpRequest in the browser and an instance of
      // http.ClientRequest in node.js
      console.log(error.request);
    } else {
      // Something happened in setting up the request that triggered an Error
      console.log('Error', error.message);
    }
    console.log(error.config);
  }) 
}

     useEffect(() => {
              navigate('/cart')
              setDeleted(false)
        }, [deleted])

return (

 <>

  <div className='cart'>
    <div className='your-cart'>
     <h2> Your Cart</h2>
    </div>
 {cart.map((item => {
   const {id, cartItem, title, price, imageUrl} = item;
  return (
  <div className="cart-item">
    <div className="cart-item-left">
    <h2>{title}</h2>
     <h4>Quantity: {cartItem.quantity}</h4>
     <button id={id} onClick={handleSubmit} className="default-button">Delete from cart</button>
     </div> 

      <div className="cart-item-right">
      <img src={imageUrl}/>
      </div> </div>
  )
 }))}

 </div>

 </>
  );
};

export default CartContents;
