import React, {useState, useRef, useEffect} from 'react';
import { useGlobalContext } from '../context/Context';
import axios from "axios";    
import { useNavigate, useParams } from "react-router-dom";
import { getByPlaceholderText } from '@testing-library/react';

const Products = () => {
const productId = useParams();
const { isLoading, searchTerm, product, redirect, setRedirect, cart, setCart, getCart} = useGlobalContext()
    const [singleProductId, setSingleProductId] = useState([])
  const [products, setProducts] = useState([])
  const navigate = useNavigate();


          const url =
    'http://localhost:8001/products'

        const getProducts =async () => {
        isLoading(true)
        try {
        const data = await axios.get(url);
        const newProducts = data.data
        setProducts(newProducts)
        isLoading(false)

        }  catch(error) {
        console.log(error);
        } }

           useEffect(()=> {
            getProducts()
        }, [])


    // get products for product details page
        useEffect(()=> {
            axios.get(`http://localhost:8001/products/${productId.productId}`)
            .then(res => {
        return res
           }).then(res => {
           setSingleProductId([res.data.id])  
                return res
            })
            .catch(err =>{
                console.log(err)
            })
        }, [productId.id])

// send post request to add item to cart, set redirect state to true.
    const handleSubmit =  e => {
    e.preventDefault();
    setRedirect(true)
    let request = {method: 'POST',
            url: "http://localhost:8001/cart",
            headers: {
                'Content-Type': 'application/json'
            },
            data: {
               productId: singleProductId[0]}}
            axios(request)
                .then(res => {
                return res
                })   
        .catch(err => console.log(err))};

        // redirects to Cart page based on whether redirect state is sett to true/false. redirect is then reset to false.
       useEffect(() => {
            if (redirect) {
              navigate('/cart')
              setRedirect(false)
            }
        }, [cart])


let filtered  = products.map((item) => { 
            const {title} = item;
            return {...item, title: title.toLowerCase()}})
            .filter((product)=> {
    return product.title.includes(searchTerm.toLowerCase())  
})

const productDetails = filtered.filter((product) => {

       return singleProductId == product.id
    })
  return (
<>
 {productDetails.map(product => {
    const {id, title, description, medImageUrl, imageUrl, price, category} = product;
    return (
<div className='product-details-main'>

          <div className='product-details'>
         <h2>{`${title.substring(0, 100)}`.toUpperCase()}</h2>
                      <div className='product-details-image'>

            <img src={medImageUrl} alt="description" />
        </div>  </div>


 <div className="product-details-right-sidebar">
<h2>Price: ${price}</h2>

<p>{description}
</p>

<button onClick={handleSubmit}>Add to cart</button>


</div>
</div>
  ) })}      



</>
  );
};

export default Products;
