import React, {useState, useEffect} from 'react';
import { useGlobalContext } from '../context/ProductsContext';
import SearchForm from './SearchForm';


const ProductsContainer = ({children}) => {
const {products, setProducts, searchTerm, loading,getProducts} = useGlobalContext()


let filtered = products.filter((product)=> product.title.includes(searchTerm))

if (loading) {
  return <div className='products'>Loading...</div>
}

return (

 <>

  <div className='products-title'>
     <SearchForm></SearchForm>
    <h2>
     {products.length} products found!</h2></div> 

 <div className='products'>
   <div className="categories-sidebar"><h2>Categories</h2>
   <ul>
     <li>Home</li>
    <li>Kitchen</li>
     <li>Music</li>
     <li>Electronics</li>
     <li>Food</li>

   </ul>
   </div>
    <div className='products-container'>



{filtered.map((product) => {
    const {id, title, description, image, price} =product;

  setProducts(filtered)
       return (
       <>
       <div key={id} className='product'>
         <h2>  {`${title.substring(0, 70)}...`}</h2>
          <h3> ${price}</h3>

          <section className='description'><img src={image} alt="" />
         </section></div>
          </>
       ) 
//  }  else {

//     setProducts([])
//    return <div>sdsdfasdaf</div>

//  }

  }

  )}

</div>
 </div>

 </>
  );
};

export default ProductsContainer;
