import React, {useState, useEffect} from 'react';
import { useGlobalContext } from '../context/ProductsContext';


const ProductsContainer = ({children}) => {
const {products, setProducts, loading} = useGlobalContext()

if (loading) {
  return <div className='products'>Loading...</div>
}
  return (

 <>

 <div className='products'>
   <div className='products-title'><h2></h2></div>

   <div className="categories-sidebar"><h2>Categories</h2>
   <ul>
     <li>Home</li>
    <li>Kitchen</li>
     <li>Music</li>
     <li>Electronics</li>
     <li>Food</li>

   </ul>
   </div>
    <div className='products-container'>

     {products.map((product) => {
    const {id, title, description, image, price} =product;
       return (
       <>
       <div key={id} className='product'>
         <h2> {title}</h2>
          <h3> ${price}</h3>

          <section className='description'><img src={image} alt="" />
          <p>{description}</p></section></div>
          </>
       )
     })}
</div>
 </div>

 </>
  );
};

export default ProductsContainer;
