console.log(cocktails)

  if (loading) {
    return (
    <div>
<Loading></Loading>  
  </div>
  )

}
 if (cocktails.length <1 ) {
   return (
     <div>no cocktails matched your search critera</div>
   )
 } 
 else {
  return (
<Paragraph>
<h1>Cocktails</h1>
{cocktails.map((cocktail)=> {
