 {cart.map((item => {
   const {cartItem, title, price, imageUrl} = item;
   console.log(item)
  return (
  <div className="cart-item">
    <div className="cart-item-left">
    <h2>{title}</h2>
     <h4>Quantity: {cartItem.quantity}</h4>
     <button className="default-button">Delete from cart</button>
     </div> 

