import React, {useState, useCallback, useContext, useEffect} from 'react'
import reducer from './reducer'

const AppContext = React.createContext();

const AppProvider = ({children}) => {

        const [loading, isLoading] = useState(false)
        // sets up state for Loading component
        const [searchTerm, setSearchTerm] = useState('')
        const [products, setProducts] = useState([])
        const [show, setShow] = useState(false);

        const [cart, setCart] = useState([])

        const [deleted, setDeleted] = useState(false)
        const [redirect, setRedirect] = useState(false)


   const cartUrl =
    'http://localhost:8001/cart'



   const getCart = async () => {
        isLoading(true)
        const data = await axios.get(cartUrl);
        const newCart = data.data
        setCart(newCart)
        isLoading(false)
        return cart       
          .catch(error => {
        console.log(error);
        }) .catch(function (error) {
    if (error.response) {
      // The request was made and the server responded with a status code
      // that falls out of the range of 2xx
      console.log(error.response.data);
      console.log(error.response.status);
      console.log(error.response.headers);
    } else if (error.request) {
      // The request was made but no response was received
      // `error.request` is an instance of XMLHttpRequest in the browser and an instance of
      // http.ClientRequest in node.js
      console.log(error.request);
    } else {
      // Something happened in setting up the request that triggered an Error
      console.log('Error', error.message);
    }
    console.log(error.config);
  }) }

           useEffect(()=> {
            getCart()
        }, [redirect]) 

         useEffect(()=> {
            getCart()
        }, [deleted]) 



          const url =
    'http://localhost:8001/products'

        const getProducts =async () => {
        isLoading(true)
        try {
        const data = await axios.get(url);
        const newProducts = data.data
        setProducts(newProducts)
        isLoading(false)

        }  catch(error) {
        console.log(error);
        } }

           useEffect(()=> {
            getProducts()
        }, [])


let filtered  = products.map((item) => { 
            const {title} = item;
            return {...item, title: title.toLowerCase()}})
            .filter((product)=> {
    return product.title.includes(searchTerm.toLowerCase())  
})

console.log(filtered)

