    return<>
    <AppContext.Provider value={{
        loading,
        searchTerm,
        products,
        setSearchTerm,
        getProducts,
        filtered,
        show,
        setShow,
        cart,
        setCart,
        getCart,
        redirect,
        setRedirect,
        getCart,
        deleted, 
        setDeleted
        // handleClick

    }}> {children}
    </AppContext.Provider>
    </>
}

export const useGlobalContext = () => {
return useContext(AppContext)
}

